import React from 'react';
import { Container, Typography, Box, Paper, Avatar, Divider, TextField, Button, Card, CardContent } from '@mui/material';
import { ThumbUp, Comment } from '@mui/icons-material';

const PostDetail = () => {
  // Mock data - would come from API in real implementation
  const post = {
    id: 1,
    title: "Introduction to React Hooks",
    description: "React Hooks are a powerful feature that allows you to use state and other React features without writing a class. In this post, I'll show you how to use useState and useEffect hooks.",
    createdAt: "2025-03-20T10:30:00",
    category: "CODING",
    owner: {
      username: "techuser123",
      profilePicture: "https://i.pravatar.cc/300"
    },
    likes: 42,
    comments: [
      {
        id: 1,
        user: {
          username: "codeenthusiast",
          profilePicture: "https://i.pravatar.cc/300?img=2"
        },
        content: "Great explanation! I've been struggling with hooks.",
        createdAt: "2025-03-20T11:15:00"
      },
      {
        id: 2,
        user: {
          username: "reactfan99",
          profilePicture: "https://i.pravatar.cc/300?img=3"
        },
        content: "Could you also explain useContext in your next post?",
        createdAt: "2025-03-20T14:22:00"
      }
    ]
  };

  return (
    <Container maxWidth="md" sx={{ mt: 4, mb: 4 }}>
      <Paper elevation={3} sx={{ p: 3 }}>
        {/* Post Header */}
        <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
          <Avatar 
            src={post.owner.profilePicture} 
            alt={post.owner.username}
            sx={{ mr: 2 }}
          />
          <Box>
            <Typography variant="subtitle1" sx={{ fontWeight: 'bold' }}>
              {post.owner.username}
            </Typography>
            <Typography variant="caption" color="text.secondary">
              {new Date(post.createdAt).toLocaleDateString()} • {post.category.replace('_', ' ')}
            </Typography>
          </Box>
        </Box>

        {/* Post Content */}
        <Typography variant="h5" component="h1" gutterBottom sx={{ mt: 2 }}>
          {post.title}
        </Typography>
        <Typography variant="body1" paragraph>
          {post.description}
        </Typography>

        {/* Media would go here */}
        <Box sx={{ my: 2, height: 300, bgcolor: 'grey.200', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
          <Typography>Media Content Placeholder</Typography>
        </Box>

        {/* Engagement */}
        <Box sx={{ display: 'flex', alignItems: 'center', my: 2 }}>
          <Button startIcon={<ThumbUp />} sx={{ mr: 2 }}>
            {post.likes} Likes
          </Button>
          <Typography variant="body2">
            {post.comments.length} Comments
          </Typography>
        </Box>

        <Divider sx={{ my: 2 }} />

        {/* Comments Section */}
        <Typography variant="h6" gutterBottom>
          Comments
        </Typography>
        
        {/* Add a comment */}
        <Box sx={{ display: 'flex', alignItems: 'flex-start', mb: 3 }}>
          <Avatar sx={{ mr: 2, mt: 1 }} />
          <TextField
            fullWidth
            variant="outlined"
            placeholder="Add a comment..."
            multiline
            rows={2}
            sx={{ mr: 2 }}
          />
          <Button variant="contained" sx={{ mt: 1 }}>
            Post
          </Button>
        </Box>

        {/* Comments list */}
        {post.comments.map(comment => (
          <Card key={comment.id} variant="outlined" sx={{ mb: 2 }}>
            <CardContent>
              <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
                <Avatar 
                  src={comment.user.profilePicture}
                  alt={comment.user.username}
                  sx={{ mr: 2, width: 32, height: 32 }}
                />
                <Box>
                  <Typography variant="subtitle2">
                    {comment.user.username}
                  </Typography>
                  <Typography variant="caption" color="text.secondary">
                    {new Date(comment.createdAt).toLocaleDateString()}
                  </Typography>
                </Box>
              </Box>
              <Typography variant="body2">
                {comment.content}
              </Typography>
            </CardContent>
          </Card>
        ))}
      </Paper>
    </Container>
  );
};

export default PostDetail;
